import React, { useState } from "react";
import "./sidebar.css"; 
const Sidebar = () => {
  const [submenuActive, setSubmenuActive] = useState(false);

  const handleSidebarToggle = () => {
    setSubmenuActive(false);

  };

  const handleMenuItemClick = (index) => {
    setSubmenuActive(!submenuActive);

  };

  const handleSubmenuTitleClick = () => {
    setSubmenuActive(false);

  };

  return (
    <>
      <div className="sidebar">
        <div className="d-flex align-items-center justify-content-between">
            <a href="#" className="logo">
            mashPlannr.
            </a>
            <i style={{fontSize: "17px", color: "white", cursor: "pointer"}} className="fa-solid fa-bars" id="sidebar-close" onClick={handleSidebarToggle}></i>
        </div>

        <div className="menu-content">
          <ul className="menu-items">

            <li className="item" onClick={() => handleMenuItemClick(0)}>
              <a href="#">Your first link</a>
            </li>

            <li className={`item ${submenuActive ? "show-submenu" : ""}`}>

              <ul className={`menu-items submenu ${submenuActive ? "submenu-active" : ""}`}>
                <div className="menu-title" onClick={handleSubmenuTitleClick}>
                  <i className="fa-solid fa-chevron-left"></i>
                  Your submenu title
                </div>
                <li className="item">
                  <a href="#">Dashboard</a>
                </li>
                {''}
              </ul>
            </li>
            {''}
            <li className="item" onClick={() => handleMenuItemClick(2)}>
              <a href="#">Merchants</a>
            </li>

            <li className="item" onClick={() => handleMenuItemClick(3)}>
              <a href="#">Your third link</a>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
