import React, { useState } from 'react';
import './styles.css'; 

const Sidebar = () => {
  const [isNavOpen, setIsNavOpen] = useState(false);

  const toggleNavbar = () => {
    setIsNavOpen((prevIsNavOpen) => !prevIsNavOpen);
  };

  const handleLinkClick = () => {

  };

  return (
    <div id="body-pd">
      <header className="header" id="header">
        <div className="header_toggle">
          <i className={`bx ${isNavOpen ? 'bx-x' : 'bx-menu'}`} onClick={toggleNavbar}></i>
        </div>
        <div className="header_img">
          <img src="https://i.imgur.com/hczKIze.jpg" alt="" />
        </div>
      </header>
      <div className={`l-navbar ${isNavOpen ? 'show' : ''}`} id="nav-bar">
        <nav className="nav">
          <div>
            <a href="#" className="nav_logo">
              <i className="bx bx-layer nav_logo-icon"></i>
              <span className="nav_logo-name">BBBootstrap</span>
            </a>
            <div className="nav_list">
              <a href="#" className="nav_link active" onClick={handleLinkClick}>
                <i className="bx bx-grid-alt nav_icon"></i>
                <span className="nav_name">Dashboard</span>
              </a>
              {''}
            </div>
          </div>
          <a href="#" className="nav_link" onClick={handleLinkClick}>
            <i className="bx bx-log-out nav_icon"></i>
            <span className="nav_name">SignOut</span>
          </a>
        </nav>
      </div>
      {''}
      <div className="height-100 bg-light">
        <h4>Main Components</h4>
      </div>
      {''}
    </div>
  );
};

export default Sidebar;
