import React, { useState } from "react";
import { Icon } from "boxicons";
import "./sidebar.css";

const Sidebar = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(true);
  const [submenuActive, setSubmenuActive] = useState(false);

  const handleSidebarToggle = () => {
    setSubmenuActive(!submenuActive);
    setSidebarOpen((prevOpen) => !prevOpen);
  };

  const handleMenuItemClick = (index) => {
    setSubmenuActive(!submenuActive);
  };

  const handleSubmenuTitleClick = () => {
    setSubmenuActive(false);
  };

  return (
    <>

      <div className="sidebar" style={{width: submenuActive?"260px":"50px"}}>
        <div className="d-flex align-items-center justify-content-between">
        {submenuActive&&
            <a href="#" className="logo">
                mashPlannr.
            </a>
        }
          <i
            style={{ fontSize: "17px", color: "white", cursor: "pointer", position: "fixed", top: "25px", left:  submenuActive?"220px":"15px"}}
            className="fa-solid fa-bars"
            id="sidebar-close"
            onClick={handleSidebarToggle}
          ></i>
        </div>

        <div className="menu-content">
          <ul className="menu-items">
            <div className="sidebar-icons">
                <li className="item d-flex align-center">
                <box-icon type='solid' name='dashboard' style={{position: !submenuActive&&"fixed", left: !submenuActive&&"10px", top: !submenuActive&&"110px"}}></box-icon><a href="/">Dashboard</a>
                </li>

                <li className="item d-flex align-center" onClick={() => handleMenuItemClick(2)}>
                <box-icon type='solid' name='group' style={{position: !submenuActive&&"fixed", left: !submenuActive&&"10px", top: !submenuActive&&"120px"}}></box-icon><a href="/merchants">Merchants</a>
                </li>

                <li className="item d-flex align-center" onClick={() => handleMenuItemClick(3)}>
                <box-icon type='solid' name='truck' style={{position: !submenuActive&&"fixed", left: !submenuActive&&"10px", top: !submenuActive&&"110px"}}></box-icon><a href="#">Orders</a>
                </li>

                <li className="item d-flex align-center" onClick={() => handleMenuItemClick(4)}>
                <box-icon type='solid' name='calendar-check' style={{position: !submenuActive&&"fixed", left: !submenuActive&&"10px", top: !submenuActive&&"110px"}}></box-icon> <a href="#">Leave Management</a>
                </li>
         </div>
          </ul>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
